"""

    val up = '('
    val down = ')'

    fun parseInput(input: String): Int {
        val list = input.toList().filterNot { it.isWhitespace() }
        return list.count { it == up } - list.count { it == down }
    }

    val floorGround = 0
    val floorBasement = -1

    fun findBasementPos(input: String): Int {
        val list = input.toList().filterNot { it.isWhitespace() }
        var floor = floorGround
        for ((i, v) in list.withIndex()) {
            when (v) {
                up -> floor += 1
                down -> floor -= 1
                else -> IllegalArgumentException("unknown: $v at pos $i")
            }
            if (floor == floorBasement) return i + 1 // first pos = 1
        }
        throw IllegalArgumentException("input string doesn't go to basement")
    }
}

